// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/presets/ERC20PresetMinterPauser.sol";
import "./interfaces/ISelendraV2.sol";

contract SelendraHandle {
    ERC20PresetMinterPauser public wrappedSelendra;
    ISelendraV2 public selendraV2;

    constructor(address _wrappedSelendra, address _selendraV2) {
        wrappedSelendra = ERC20PresetMinterPauser(_wrappedSelendra);
        selendraV2 = ISelendraV2(_selendraV2);
    }

    function swapBridge(uint256 amount) external {
        selendraV2.transferFrom(msg.sender, address(this), amount);
        wrappedSelendra.mint(msg.sender, amount);
    }

    function withdraw(uint256 amount) external {
        require(wrappedSelendra.balanceOf(msg.sender) >= amount, "balance too low");
        wrappedSelendra.burnFrom(msg.sender, amount);
        selendraV2.transfer(msg.sender, amount);
    }

    modifier isSelendraToken(address token) {
        require(token == address(selendraV2), "token is selendra v2");
        _;  
    }
}
